function Contact() {
  const email = "karthanvenkateshvenkatesh@gmail.com";
  const github = "https://github.com/venkatesh5650";
  const linkedin = "https://www.linkedin.com/in/karthan-venkatesh/";
  const phone = "+91 93923 72089";
  const mailToLink = `mailto:${encodeURIComponent(email)}`;

  return (
    <section className="section">
      <div className="container">
        <h2 className="section-title mb-3">Contact</h2>
        <p className="section-subtitle mb-4">
          Interested in working together or have any questions? Feel free to
          reach out.
        </p>

        <div className="row">
          <div className="col-md-6 mb-4">
            <h5 className="mb-3">Let&apos;s Connect</h5>

            <p className="mb-1">
              <strong>Email:</strong>{" "}
              <a href={mailToLink} className="contact-link">
                {email}
              </a>
            </p>

            <p className="mb-1">
              <strong>Phone:</strong>{" "}
              <a
                href={`tel:${phone.replace(/\s/g, "")}`}
                className="contact-link"
              >
                {phone}
              </a>
            </p>

            <p className="mb-1">
              <strong>GitHub:</strong>{" "}
              <a
                href={github}
                target="_blank"
                rel="noreferrer"
                className="contact-link"
              >
                {github}
              </a>
            </p>

            <p className="mb-1">
              <strong>LinkedIn:</strong>{" "}
              <a
                href={linkedin}
                target="_blank"
                rel="noreferrer"
                className="contact-link"
              >
                {linkedin}
              </a>
            </p>

            <p className="mb-0">
              <strong>Location:</strong> Hyderabad, India
            </p>
          </div>

          <div className="col-md-6">
            <h5 className="mb-3">Quick Message</h5>
            <p className="text-muted">
              You can send me an email directly using the button below. I&apos;m
              open to internship opportunities, full-time roles, and
              collaboration on interesting projects.
            </p>

            <button
              className="btn btn-primary"
              type="button"
              onClick={() => {
                window.open(
                  "mailto:karthanvenkateshvenkatesh@gmail.com",
                  "_blank"
                );
              }}
            >
              Send Email
            </button>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
